mapper.select:
select s.id, s.name, s.category_id, s.price, s.image, s.description, s.status, s.update_time, c.name as categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%', #{setmealPageQueryDTO.name}, '%')
and s.category_id = #{setmealPageQueryDTO.categoryId}
and s.status = #{setmealPageQueryDTO.status}
order by s.id desc
